</span><span class=\"pln\"> is_tag</span><span class=\"pun\">()</span><span class=\"pln\"> </span><span class=\"pun\">)</span><span class=\"pln\"> </span><span class=\"pun\">{</span></li>\r\n 	<li class=\"L1\"><span class=\"pln\">$term_id </span><span class=\"pun\">=</span><span class=\"pln\"> get_query_var</span><span class=\"pun\">(</span><span class=\"str\">\'tag_id\'</span><span class=\"pun\">);</span></li>\r\n 	<li class=\"L2\"><span class=\"kwd\">if</span><span class=\"pln\"> </span><span class=\"pun\">(</span><span class=\"pln\"> get_option</span><span class=\"pun\">(</span><span class=\"pln\"> </span><span class=\"str\">\'tag-title-\'</span><span class=\"pun\">.</span><span class=\"pln\">$term_id </span><span class=\"pun\">))</span><span class=\"pln\"> </span><span class=\"pun\">:</span><span class=\"pln\"> </span><span class=\"pun\">?&gt;</span></li>\r\n 	<li class=\"L3\"><span class=\"tag\">&lt;title&gt;</span><span class=\"pun\">&lt;?</span><span class=\"pln\">php echo get_option</span><span class=\"pun\">(</span><span class=\"pln\"> </span><span class=\"str\">\'tag-title-\'</span><span class=\"pun\">.</span><span class=\"pln\">$term_id </span><span class=\"pun\">);</span><span class=\"pln\"> </span><span class=\"pun\">?&gt;&lt;?</span><span class=\"pln\">php </span><span class=\"kwd\">if</span><span class=\"pln\"> </span><span class=\"pun\">(</span><span class=\"pln\">get_query_var</span><span class=\"pun\">(</span><span class=\"str\">\'paged\'</span><span class=\"pun\">))</span><span class=\"pln\"> </span><span class=\"pun\">{</span><span class=\"pln\"> echo stripslashes</span><span class=\"pun\">(</span><span class=\"pln\">get_option</span><span class=\"pun\">(</span><span class=\"str\">\'ygj_lianjiefu\'</span><span class=\"pun\">));</span><span class=\"pln\">echo </span><span class=\"str\">\'第\'</span><span class=\"pun\">;</span><span class=\"pln\"> echo get_query_var</span><span class=\"pun\">(</span><span class=\"str\">\'paged\'</span><span class=\"