'第\'</span><span class=\"pun\">;</span><span class=\"pln\"> echo get_query_var</span><span class=\"pun\">(</span><span class=\"str\">\'paged\'</span><span class=\"pun\">);</span><span class=\"pln\"> echo </span><span class=\"str\">\'页\'</span><span class=\"pun\">;}?&gt;&lt;?</span><span class=\"pln\">php echo stripslashes</span><span class=\"pun\">(</span><span class=\"pln\">get_option</span><span class=\"pun\">(</span><span class=\"str\">\'ygj_lianjiefu\'</span><span class=\"pun\">));</span><span class=\"pln\"> </span><span class=\"pun\">?&gt;&lt;?</span><span class=\"pln\">php bloginfo</span><span class=\"pun\">(</span><span class=\"str\">\'name\'</span><span class=\"pun\">);</span><span class=\"pln\"> </span><span class=\"pun\">?&gt;</span><span class=\"tag\">&lt;/title&gt;</span></li>\r\n 	<li class=\"L4\"><span class=\"pun\">&lt;?</span><span class=\"pln\">php </span><span class=\"kwd\">else</span><span class=\"pun\">:</span><span class=\"pln\"> </span><span class=\"pun\">?&gt;</span></li>\r\n 	<li class=\"L5\"><span class=\"tag\">&lt;title&gt;</span><span class=\"pun\">&lt;?</span><span class=\"pln\">php single_tag_title</span><span class=\"pun\">(</span><span class=\"str\">\"\"</span><span class=\"pun\">,</span><span class=\"pln\"> </span><span class=\"kwd\">true</span><span class=\"pun\">);</span><span class=\"pln\"> </span><span class=\"pun\">?&gt;&lt;?</span><span class=\"pln\">php </span><span class=\"kwd\">if</span><span class=\"pln\"> </span><span class=\"pun\">(</span><span class=\"pln\">get_query_var</span><span class=\"pun\">(</span><span class=\"str\">\'paged\'</span><span class=\"pun\">))</span><span class=\"pln\"> </span><span class=\"pun\">{</span><span class=\"pln\"> echo stripslashes</span><span class=\"pun\">(</span><span class=\"pln\">get_option</span><span class=\"pun\">(</span><span class=\"str\">\'ygj_lianjiefu\'</span><span class=\"pun\">));</span><span class=\"pln\">echo </span><span class=\"str\">\'第\'</span><span class=\"pun\">;</span>